import styled from "styled-components";

const ButtonSSS = styled.button`
  background-color: lightgray;
  color: purple;
  cursor: pointer;
  padding: 1rem 1.5rem;
  border: 2px solid blueviolet;
  font-family: "tahoma";
  &:hover {
    transform: scale(0.97);
  }
`;

const domatesButon = styled(ButtonSSS)``;

export default ButtonSSS;
